/*Table structure for table `userinfo` */

DROP TABLE IF EXISTS `userinfo`;

CREATE TABLE `userinfo` (
  `id` varchar(64) NOT NULL COMMENT '用户信息表id标识',
  `appId` varchar(32) DEFAULT NULL COMMENT 'appid',
  `username` varchar(32) NOT NULL COMMENT '用户登录名',
  `realname` varchar(32) NOT NULL COMMENT '真实姓名',
  `avatarUrl` varchar(255) DEFAULT NULL COMMENT '头像',
  `ename` varchar(32) DEFAULT NULL COMMENT '英文名',
  `pwd` varchar(32) DEFAULT NULL COMMENT '登录密码',
  `token` varchar(255) DEFAULT NULL COMMENT '第三方token',
  `tel` varchar(32) DEFAULT NULL COMMENT '电话',
  `fax` varchar(32) DEFAULT NULL COMMENT '传真',
  `email` varchar(32) DEFAULT NULL COMMENT '邮件',
  `mobile` varchar(32) DEFAULT NULL COMMENT '移动电话号码',
  `qq` varchar(32) DEFAULT NULL COMMENT 'QQ',
  `balance` double DEFAULT '0' COMMENT '余额',
  `state` int(11) DEFAULT NULL COMMENT '账号状态：0未启用，1正常，2、异常，3冻结',
  `idCard` varchar(255) DEFAULT NULL COMMENT '身份证号码',
  `provinceCode` varchar(32) DEFAULT NULL COMMENT '省',
  `provinceName` varchar(64) DEFAULT NULL COMMENT '省',
  `cityCode` varchar(32) DEFAULT NULL COMMENT '市',
  `cityName` varchar(64) DEFAULT NULL COMMENT '市',
  `regionCode` varchar(32) DEFAULT NULL COMMENT '区',
  `regionName` varchar(64) DEFAULT NULL COMMENT '区',
  `address` varchar(256) DEFAULT NULL COMMENT '详细地址',
  `remark` varchar(64) DEFAULT NULL COMMENT '备注',
  `cdate` datetime DEFAULT NULL COMMENT '创建时间',
  `mdate` datetime DEFAULT NULL COMMENT '最后修改时间',
  `creator` varchar(64) DEFAULT NULL COMMENT '创建人',
  `modify` varchar(64) DEFAULT NULL COMMENT '修改人',
  `version` varchar(64) DEFAULT '0' COMMENT '数据版本',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='用户信息表';

